template.value:
const app = new Vue({
            el: '#app', //element,vue作用的标签
            data: {
                message: "Hello"
            }  
        });

const vm = new Vue({
        el: '#app',
        data: {
            site: "菜鸟教程",
            url: "www.runoob.com",
            alexa: "10000",
            kilometers: 0,
            meters:0
        },
        //方法
        methods: {
            details: function () {
                return this.site + " - 学的不仅是技术，更是梦想！";
            }
        },
        //计算属性
        computed: {
            // 计算属性的 getter
            reversedMessage: function () {
                // `this` 指向 vm 实例
                return this.message.split('').reverse().join('')
            }
        },
        //监听属性
        watch : {
            kilometers:function(val) {
                this.kilometers = val;
                this.meters = this.kilometers * 1000
            },
            meters : function (val) {
                this.kilometers = val/ 1000;
                this.meters = val;
            }
        }
    })
